class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        Map<Integer, Long> s[] = new Map[n];
        Arrays.setAll(s, e->new HashMap<>());
        s[0].put(nums[0], 1L);
        long ans = nums[0] == k ? 1 : 0;
        for(int i = 1; i < n; ++i) {
            for(int j : s[i - 1].keySet()) {
                int x = j & nums[i];
                s[i].put(x, s[i].getOrDefault(x, 0L) + s[i - 1].get(j));
            }
            s[i].put(nums[i], s[i].getOrDefault(nums[i], 0L) + 1);
            ans += s[i].getOrDefault(k, 0L);
        }
        return ans;
    }
}
