class Solution {
public:
    using ll = long long;
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, ll> prev;
        ll ans = 0;
        prev[nums[0]] = 1;
        if (nums[0] == k) ++ans;
        for (int i = 1; i < n; ++i) {
            unordered_map<int, ll> cur;

            cur[nums[i]] = 1;

            for (auto [k, v] : prev) {
                cur[k & nums[i]] += v;
            }
            if (cur.contains(k)) ans = ans + cur[k];
            prev = std::move(cur);
        }
        return ans;
    }
};
