class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        def update_count(prev_map, num, target):
            current_map = defaultdict(int)
            if num == target:
                result[0] += 1
            current_map[num] = 1
            for key, count in prev_map.items():
                bitwise_and_result = key & num
                if bitwise_and_result == target:
                    result[0] += count
                current_map[bitwise_and_result] += count
            return current_map

        n = len(nums)
        prev_map = defaultdict(int)
        result = [0]

        for num in nums:
            prev_map = update_count(prev_map, num, k)

        return result[0]
