typedef long long ll;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0, n = nums.size();
        unordered_map <ll,ll>prev;
        for(int i = 0; i < n; i++){
            ll num = nums[i];
            unordered_map <ll,ll> cur;
            if(num == k)ans++;
            cur[num] = 1;
            for(auto it : prev){
                ll v = it.first, f = it.second,t = v&num;
                if(t == k)ans += f;
                cur[t] += f;
            }
            prev = cur;
        }
        return ans;
    }
};
