class Solution { 
public: 
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size();  
        unordered_map<long long, long long> aa; 
        long long cnt = 0;
        for (int i = 0; i < n; ++i) { 
            unordered_map<long long, long long> bb;  
            if (nums[i] == k) { 
                cnt++; 
            } 
            bb[nums[i]] = 1; 

            for (auto& [val, freq] : aa) { 
                long long next = val & nums[i]; 
                if (next == k) { 
                    cnt += freq; 
                } 
                bb[next] += freq; 
            }    
            aa = bb; 
        }   
        return cnt; 
    } 
};
