class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    long long res = 0;
    unordered_map<long long, long long> gcds;
    for (int i = 0; i < nums.size(); ++i) {
        unordered_map<long long, long long> gcds1;
        if ((nums[i] & k) == k) {
            ++gcds[nums[i]];
            for (auto [prev_gcd, cnt] : gcds)
                gcds1[prev_gcd & nums[i]] += cnt;
        }
        res += gcds1[k];
        swap(gcds, gcds1);
    }
    return res;

    }
};
