#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        int size = arr.size();
        long long totalCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults;

            if (arr[index] == target) {
                ++totalCount;
            }
            currentResults[arr[index]] = 1;

            for (const auto& [previousVal, frequency] : previousResults) {
                long long newAndResult = calculateAndResult(previousVal, arr[index]);
                if (newAndResult == target) {
                    totalCount += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return totalCount;
    }

private:
    long long calculateAndResult(long long a, long long b) {
        return a & b;
    }
};
