class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<long long>> pos(n,vector<long long>(32,n));
        for(int i=n-1;i>=0;i--)
        {
            if(i+1<n)
            pos[i]=pos[i+1];
            int x=nums[i];
            int c=0;
            while(c<31)
            {
                 if(x%2==0)
                pos[i][c]=i;
                ++c;
                x/=2;
            }
            // if(nums[i]==k)
            // pos[i]=i;
        }
        int cnt=0;
        vector<long long> pre(n);
        for(int i=n-1;i>=0;i--)
        {
            if((nums[i]&k)==k)
            ++cnt;
            else
            cnt=0;
            pre[i]=cnt;
        }
        long long ans=0;
        for(int i=0;i<n;i++)
        {
             int x=1;
             int y=1;
             long long en=n;
             long long st=i;
             int j=0;
             while(j<31)
             {

                if((x&k))
                {
                    en=min(en,pos[i][j]);

                }
                else
                st=max(st,pos[i][j]);
                if(j==30)
                break;
                ++j;
                x*=2;
             }
             //cout<<en<<" "<<st<<endl;
            ans+=max(0LL,en-st);
           // cout<<ans<<" ";
        }
        return ans;

    }
};
