class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        j = 0
        n = len(nums)
        counts = [0] * 32
        for i in range(n):
            if (nums[i] & k) < k:
                return res + self.countSubarrays(nums[i+1:], k)

            x = 1
            for b in range(32):
                counts[b] += 1 if (nums[i] & x) else 0
                x <<= 1

            curr = int(''.join(['1' if x == i - j + 1 else '0' for x in reversed(counts)]), 2)
            while curr == k:
                x = 1
                for b in range(32):
                    counts[b] -= 1 if (nums[j] & x) else 0
                    x <<= 1
                j += 1
                curr = int(''.join(['1' if x == i - j + 1 else '0' for x in reversed(counts)]), 2)

            res += j

        return res
