class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long res = 0;
        int n = a.size();
        vector<array<int, 3>> dp;
        for (int pos = 0; pos < a.size(); pos++) {
            int cur = a[pos];
            for (int i = 0; i < dp.size(); i++) dp[i][2] = (dp[i][2] & cur);
            dp.push_back({ pos, pos, cur });
            int ptr = 0;
            for (int j = 1; j < dp.size(); j++) {
                if (dp[j][2] != dp[ptr][2]) dp[++ptr] = dp[j];
                else dp[ptr][1] = dp[j][1];
            }
            dp.resize(ptr + 1);
            {
                for (auto [l, r, x] : dp) {
                    if (x == k) res += r - l + 1;
                }
            }
        }
        return res;
    }
};
