#define ll long long
#define rep(i,n) for(int i=0;i<n;i++)
#define che(a,b) if (a== b)
class Solution
{
public:
    void solve(unordered_map<ll, ll> &pR, vector<int> &nums, unordered_map<ll, ll> &cR, int k, int i, ll &res)
    {
        for (auto in : pR)
        {
            if ((in.first & nums[i]) == k)
            {
                res += in.second;
            }
            cR[(in.first & nums[i])] += in.second;
        }
    }

    ll countSubarrays(vector<int> &nums, int k)
    {
        ll n = nums.size(),res = 0;
        unordered_map<ll, ll> pR;

        rep(i,n)
        {
            unordered_map<ll, ll> cR;
            che(nums[i],k)++res;
            cR[nums[i]] = 1;
            solve(pR, nums, cR, k, i, res);
            pR = cR;
        }

        return res;
    }
};
