class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ct=0;

        unordered_map<ll,ll>lst;

        for(int i=0;i<n;i++){
            unordered_map<ll,ll>curr;

            curr[nums[i]]=1;

            if(nums[i]==k) ct++;



            for(auto it:lst){

                ll val=it.first&nums[i];
                if(val==k) ct+=it.second;
                curr[val]+=it.second;
            }
            lst=curr;
        }
        return ct;
    }
};
