class Solution {
    public long countSubarrays(int[] nums, int k) {
         int n = nums.length;
        long totalCount = 0;

        Map<Integer, Integer> countMap = new HashMap<>();
        for (int num : nums) {
            Map<Integer, Integer> newCountMap = new HashMap<>();
            newCountMap.put(num, newCountMap.getOrDefault(num, 0) + 1);

            for (Map.Entry<Integer, Integer> entry : countMap.entrySet()) {
                int value = entry.getKey();
                int cnt = entry.getValue();
                int newAnd = value & num;
                newCountMap.put(newAnd, newCountMap.getOrDefault(newAnd, 0) + cnt);
            }

            countMap = newCountMap;
            totalCount += countMap.getOrDefault(k, 0);
        }

        return totalCount;
    }
}
