class Solution:
    def countSubarrays(self, nums, k):
        n = len(nums)
        count = 0
        prefix_and = {}

        for i in range(n):
            current_and = {}

            if nums[i] == k:
                count += 1
            current_and[nums[i]] = 1

            for key, val in prefix_and.items():
                new_and = key & nums[i]
                if new_and == k:
                    count += val
                if new_and in current_and:
                    current_and[new_and] += val
                else:
                    current_and[new_and] = val

            prefix_and = current_and

        return count
