#define ll long long
vector<ll> indices[31];
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ans=0;
        int bits[30]={0};
        for(int i=0;i<30;i++){
            indices[i].clear();
            if((k&(1<<i))!=0) bits[i]=1;
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<30;j++){
                if((nums[i]&(1<<j))==0) indices[j].push_back(i);
            }
        }
        for(int i=0;i<30;i++) indices[i].push_back(n);
        for(int i=0;i<n;i++){
            ll mini=n,maxi=i;
            for(int j=0;j<30;j++){
                int u=lower_bound(indices[j].begin(),indices[j].end(),i)-indices[j].begin();
                if(bits[j]) mini=min(mini,indices[j][u]);
                else maxi=max(maxi,indices[j][u]);
            }
            ans+=max(0LL,mini-maxi);
        }
        return ans;
    }
};
