class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> res; 

            if (nums[i] == k) {
                ++cnt;
            }
            res[nums[i]] = 1;

            for (auto& [val, freq] : mp) {
                long long curr = val & nums[i];
                if (curr == k) {
                    cnt += freq;
                }
                res[curr] += freq;
            }

            mp = res;
        }

        return cnt;
    }
};
