#include <bits/stdc++.h>
using namespace std;
#define pb push_back
#define ff first
#define ss second
#define pii pair<int, int>
#define ll long long int
#define vll vector<ll>
#define all(c) c.begin(), c.end()
const long long mod = 1e9 + 7;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        map<ll, ll> mp;

        for (int i = 0; i < nums.size(); i++) {
            map<ll, ll> mp1;
            mp1[nums[i]]++;

            for (auto p : mp) {
                ll val = p.ff;
                ll f = p.ss;

                if ((val & nums[i]) == k)
                    ans += f;

                mp1[nums[i] & val] += f;
            }

            if(nums[i] == k)
                ans++;

            mp = mp1;
        }
        return ans;
    }
};
