class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> mp1;

        for (int i = 0; i < n; ++i) {

            if (nums[i] == k) {
                cnt++;
            }
            unordered_map<long long, long long> mp2;

            mp2[nums[i]] = 1;

            for (auto& [v, f] : mp1) {
                long long res = v & nums[i];
                if (res == k) {
                    cnt += f;
                }
                mp2[res] += f;
            }

            mp1 = mp2;
        }

        return cnt;
    }
};
