class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        int cnt[32][n];
        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                cnt[j][i]=!(nums[i]&(1<<j));
            }
            if(i){
                for(int j=0;j<32;j++){
                    cnt[j][i]+=cnt[j][i-1];
                }
            }
        }
        long long ans=0;
        for(int i=0;i<n;i++){
            int l=i,r=n-1;
            int up=-1;
            while(l<=r){
                int mid=(l+r)/2;
                long long tmp=0;
                long long p=1;
                for(int j=0;j<32;j++){
                    int cn=cnt[j][mid];
                    if(i)cn-=cnt[j][i-1];
                    if(!cn)tmp+=p;
                    p*=2;
                }
                if(tmp>k){
                    l=mid+1;
                }
                else if(tmp<k){
                    r=mid-1;
                }
                else{
                    up=mid;
                    l=mid+1;
                }
            }
            int lw=-1;
            l=i,r=n-1;
            while(l<=r){
                int mid=(l+r)/2;
                long long tmp=0;
                long long p=1;
                for(int j=0;j<32;j++){
                    int cn=cnt[j][mid];
                    if(i)cn-=cnt[j][i-1];
                    if(!cn)tmp+=p;
                    p*=2;
                }
                if(tmp>k){
                    l=mid+1;
                }
                else if(tmp<k){
                    r=mid-1;
                }
                else{
                    lw=mid;
                    r=mid-1;
                }
            }
            if(up!=-1){
                ans+=(up-lw+1);
            }
        }
        return ans;
    }
};
