class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int, int> mp;
        mp[INT_MAX] = 1;
        long long ans = 0;
        for (int i = 0; i < nums.size(); i++) {
            map<int, int> temp;
            temp[INT_MAX] = 1;
            for (auto j : mp) temp[j.first & nums[i]] += j.second;
            ans += temp[k];
            swap(temp, mp);
        }
        return ans;
    }
};
