class Solution {
    typedef long long ll;
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<pair<int,int>>v;
        vector<vector<pair<int,int>>>adj(n,v);
        adj[0] = {make_pair(0,nums[0])};
        ll ret = 0;
        if(nums[0] == k){
            ret++;
        }
        for(int i = 1;i<n;i++){
            int cur = nums[i];
            vector<pair<int,int>>u;
            adj[i].emplace_back(make_pair(i,nums[i]));
            int id = 0;
            for(auto obj : adj[i-1]){
                int st = obj.first,end = i-1,val = obj.second;
                cur = val&nums[i];
                if(cur == adj[i][id].second){
                    adj[i][id].first = st;
                }else{
                    adj[i].emplace_back(make_pair(st,cur));
                    id++;
                }
            }
            int end = i+1;
            for(auto obj : adj[i]){
                int st = obj.first;
                if(obj.second == k){
                    ret += (end-st);
                    break;
                }else{
                    end = st;
                }
            }
        }
        return ret;
    }
};
