class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp2; 

            if (nums[i] == k) {
                ++ans;
            }
            mp2[nums[i]] = 1;

            for (auto& [hj, cnt] : mp) {
                long long re = hj & nums[i];
                if (re == k) {
                    ans += cnt;
                }
                mp2[re] += cnt;
            }

            mp = mp2;
        }

        return ans;
    }
};
