class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long>last;
        long long res = 0;
        int n=nums.size();
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long>curr; 

            if (nums[i] == k) {
                res++;
            }
            curr[nums[i]] = 1;

            for (auto& [val, freq] : last) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    res += freq;
                }
                curr[newAndResult] += freq;
            }

            last = curr;
        }
        return res;
    }
};
