class Solution {
    #define ll long long 
    bool isTrue(int l, int k ){
        return l == k;
    }
    ll soluchan( vector<int>& nums, int k, ll n){
        ll can=0; 
        unordered_map<ll,ll> temp;
        ll i=0;
     while(i < n) {
            unordered_map<ll,ll> cur; 

            if (isTrue(nums[i],k)) {
                can++;
            }
            cur[nums[i]] = 1;

            for (auto& [val, freq] : temp) {
                ll ans = val & nums[i];
                if (ans == k) {
                    can += freq;
                }
                cur[ans] += freq;
            }

            temp = cur;
         i++;
        }
        return can;
    }
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll res = 0;
        res=soluchan(nums,k,n);


        return res;


    }
};
