class Solution:
    def countSubarrays(self, nums: List[int], tar: int) -> int:
        n = len(nums)
        ans = 0
        d = {}
        if nums[-1] >= tar:
            d[nums[-1]] = 1
            ans += nums[-1] == tar
        for i in range(n - 2, -1, -1):
            tmp = defaultdict(int)
            for k, v in d.items():
                kk = k & nums[i]
                if kk < tar:
                    continue
                tmp[kk] += v
                if kk == tar:
                    ans += v
            if nums[i] >= tar:
                tmp[nums[i]] += 1
                ans += nums[i] == tar
            d = tmp
        return ans
