class Solution {
public:
	#define ort ((bas+son)/2)
    #define mid ((start+end)/2)
	int tree[400005],a[100005];
	inline void build(int node,int start,int end){
		if(start==end){tree[node]=a[start];return ;}
		build(node*2,start,mid),build(node*2+1,mid+1,end);
		tree[node]=tree[node*2]&tree[node*2+1];
	}
	inline int query(int node,int start,int end,int l,int r){
		if(start>end || start>r || end<l)return INT_MAX;
		if(start>=l && end<=r)return tree[node];
		return query(node*2,start,mid,l,r)&query(node*2+1,mid+1,end,l,r);
	}
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        for(int i=1;i<=n;i++){
			a[i]=nums[i-1];
		}
		long long cev=0;
        build(1,1,n);
		for(int i=1;i<=n;i++){
			int bas=i;
			int son=n;
			while(bas<=son){
				if(query(1,1,n,i,ort)>=k)bas=ort+1;
				else son=ort-1;
			}
			int r=son;
			bas=i;
			son=n;
			while(bas<=son){
				if(query(1,1,n,i,ort)>k)bas=ort+1;
				else son=ort-1;
			}
			int l=bas;
			cev+=r-l+1;
		}
		return cev;
    }
};
