class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        map <int,int> ma[n + 1];
        long long ans = 0;
        for(int i = 0; i < n; i++){
            ma[i][a[i]]++;
            if(i > 0){
                for(auto to : ma[i - 1]){
                    ma[i][to.first & a[i]] += to.second;
                }
                ma[i - 1].clear();
            }

            ans += ma[i][k];
        }
        return ans;
    }
};
