class Solution {
public:
    long long countSubarrays(vector<int>& num, int k) {

        #define int long long
        vector<int> nums;
        for(int i=0;i<num.size();i++) nums.push_back(num[i]);
        int cnt = 0;
        unordered_map<int,int> s;
        map<int,int> temp;
        for (int i : nums) {
            map<int,int> r;
            r[i]++;
            for (auto j : temp){ 
                int val = i & j.first;
                r[val] += j.second;
            }
            temp = r;
            for (auto j : temp) s[j.first] += j.second;
        }
        int ans = 0;
        for(auto it : s)
        {
            if(it.first==k)
                ans += it.second;
        }

        return ans;
         #undef int

    }
};
