import copy


class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        ans = 0
        dp = Counter()
        for i in nums:
            temp = Counter()
            if i==k:
                ans+=1
            temp[i]=1
            for j in dp:
                ans += dp[j]*int(i&j==k)
                temp[i&j] += dp[j]
            dp=temp
        return ans
