class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        map<long long,long long>mp;
        for(int i=0;i<n;i++){
            map<long long,long long>curr;
            if(nums[i]==k)++cnt;
            curr[nums[i]]=1;
            for(auto&[val,freq]:mp){
                long long nwrs=val&nums[i];
                if(nwrs==k)cnt+=freq;
                curr[nwrs]+=freq;
            }
            mp=curr;
        }
        return cnt;
    }
};
