class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;
        unordered_map<long long,long long >mp1;
        for(int i=0;i<n;i++){
              unordered_map<long long,long long >mp2;
            if(nums[i]==k)count++;
            mp2[nums[i]]=1;
            for(auto x:mp1){

                long long A=x.first&nums[i];
                if(A==k)count+=x.second;

            mp2[A]+=x.second;
        }
            mp1=mp2;}
        return count;

    }
};
