class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<long long, long long> mp;
        int n = nums.size();
        for (int i = 0; i < n; i++) 
        {
            unordered_map<long long, long long> current;
            if (nums[i] == k)
                ++ans;
            current[nums[i]] = 1;
            cout<<ans<<" ";
            for (auto it:mp) {
                int v=it.first, f=it.second;
                long long y = v& nums[i];
                if (y == k)ans += f;
                current[y] += f;
            }
            mp = current;
        }

        return ans;
    }
};
