class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,long long> m;
        long long r=0;

        for(int i=0;i<nums.size();i++){
            map<int,long long> h;
            for(auto j:m){
                h[(j.first)&nums[i]]+=j.second;
            }
            h[nums[i]]++;
            m=h;
            r+=h[k];
        }
        return r;
    }
};
