class Solution {
        private long countValidSubarrays(Map<Long, Long> prevResults, long num, int k) {
        long count = 0;
        for (Map.Entry<Long, Long> entry : prevResults.entrySet()) {
            long val = entry.getKey();
            long freq = entry.getValue();

            long newAndResult = val & num;
            if (newAndResult == k) {
                count += freq;
            }
        }
        return count;
    }

    private void updateResults(Map<Long, Long> prevResults, Map<Long, Long> currResults) {
        prevResults.clear();
        prevResults.putAll(currResults);
    }

    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;

        Map<Long, Long> previousResults = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> currResults = new HashMap<>();

            if (nums[i] == k) {
                ++count;
            }
            currResults.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : previousResults.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();

                long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                currResults.put(newAndResult, currResults.getOrDefault(newAndResult, 0L) + freq);
            }

            previousResults = currResults;
        }

        return count;
    }
}
