class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans = 0;
        int n = a.size();
        vector<int> one[31], zero[31];
        for(int i=0; i<31; i++){
            one[i].resize(n + 1);
            zero[i].resize(n + 1);
        }

        for(int i=0; i<31; i++){
            one[i][n] = zero[i][n] = n;
        }

        for(int i=n-1; i>=0; i--){
            for(int j=0; j<31; j++){
                one[j][i] = one[j][i + 1];
                zero[j][i] = zero[j][i + 1];
                if(a[i] & (1LL << j))    one[j][i] = i;
                else zero[j][i] = i;
                // cout << zero[j][i] << " ";
            }
            // cout << "\n";
        }

        // cout << zero[0][1] << endl;

        for(int i=0; i<n; i++){
            int l = n - 1, r = i;
            for(int j=0; j<31; j++){
                if(k & (1LL << j)){
                    l = min(l, zero[j][i] - 1);
                }
                else{
                    r = max(r, zero[j][i]);
                }
                // cout << l << " " << r << "\n";
            }

            if((max(l, r) > n - 1) || (l < r) || (min(l, r) < i))  continue;
            ans += l - r + 1;
        }

        return ans;
    }
};
