class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> curr;
            ans += nums[i] == k;
            curr[nums[i]] = 1;
            for (auto& [val, f] : prev) {
                long long x = val & nums[i];
                if (x == k)
                    ans += f;
                curr[x] += f;
            }
            prev = curr;
        }
        return ans;
    }
};
