class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res=0
        l=[[-1,0]]
        for i in range(len(nums)):
            p=[]
            sta=nums[i]
            p.append([i,nums[i]])
            las=i
            for x in l:
                if sta==k:
                    res+=las-x[0]
                sta&=x[1]
                if sta!=p[-1][1]:
                    p.append([x[0],sta])
                las=x[0]
            p.append([-1,0])
            l=p
        return res
