class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        B = 30
        n = len(nums)
        res = 0
        cnt = [0] * B
        l, m = 0, 0
        for r in range(n):
            for i in range(B):
                cnt[i] += (nums[r] >> i) & 1
            if nums[r] & k != k:
                l = m = r + 1
                cnt = [0] * B
            while m <= r:
                and_ = 0
                for i in range(B):
                    if cnt[i] == r - m + 1:
                        and_ |= 1 << i
                if and_ == k:
                    for i in range(B):
                        cnt[i] -= (nums[m] >> i) & 1
                    m += 1
                else:
                    break
            if l <= r:
                res += m - l
        return res
