#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size();
        ll cnt=0;
        unordered_map<ll,ll> mp;
        for(ll i=0;i<n;i++){
            unordered_map<ll,ll> mp2; 
            if(nums[i]==k) cnt++;
            mp2[nums[i]]=1;
            for(auto it:mp){
                ll x=it.first;
                ll y=it.second;
                ll temp=x&nums[i];
                if(temp==k) cnt+=y;
                mp2[temp]+=y;
            }
            mp=mp2;
        }
        return cnt;
    }
};
