class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;
        unordered_map<long long, long long> previous;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> current; 

            if (nums[i] == k) {
                ++res;
            }
            current[nums[i]] = 1;

            for (auto& [val, freq] : previous) {
                long long nr = val & nums[i];
                if (nr == k) {
                    res += freq;
                }
                current[nr] += freq;
            }

            previous = current;
        }

        return res;
    }
};
