class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        mp = {-1: 1}
        for x in nums:
            tmp = defaultdict(int)
            tmp[-1] = 1
            for pre, c in mp.items():
                if pre & x & k == k:
                    tmp[pre & x] += c
            mp = tmp
            ans += mp[k]
        return ans
