class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        andCounts = Counter()
        ans = 0
        for num in nums:
            newCounts = Counter({num: 1}) 
            if num == k:
                ans += 1
            for prevAnd, cnt in andCounts.items():
                newAnd = prevAnd & num 
                if newAnd == k:
                    ans += cnt 
                newCounts[newAnd] += cnt
            andCounts = newCounts
        return ans
