class Solution {
public:
    long long countSubarrays(vector<int>& arr, int tgt) {
        int n = arr.size();
        long long count_ans = 0;
        unordered_map<long long, long long> prevResultsStore;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currResults;

            if (arr[i] == tgt) {
                ++count_ans;
            }

            currResults[arr[i]] = 1;

            for (auto& [val, freq] : prevResultsStore) {
                long long newAndResult = val & arr[i];
                if (newAndResult == tgt) {
                    count_ans += freq;
                }
                currResults[newAndResult] += freq;
            }

            prevResultsStore = currResults;
        }

        return count_ans;
    }
};
