#define ll long long
#define s second 
#define f first
class Solution {
public:
    ll countSubarrays(vector<int>& ns, int k) {
        auto sp = [](vector<int>& ns, int k) -> ll {
            int sz = ns.size();
            ll ct = 0;

            unordered_map<ll, ll> pr;

            for (int i = 0; i < sz; ++i) {
                std::unordered_map<ll, ll> cr;

                if (ns[i] == k) {
                    ++ct;
                }
                cr[ns[i]] = 1;

                for (pair<ll,ll> val : pr) {
                    ll nr = val.f & ns[i];
                        ct +=(nr == k)? val.s:0;
                    cr[nr] += val.s;
                }

                pr = cr;
            }

            return ct;
        };

        return sp(ns, k);
    }
};
