typedef long long ll;
class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        vector<vector<ll>> bits(32);
        int n = arr.size();
        for(int i=0; i<n; i++){
            for(int j=0; j<=30; j++){
                if((arr[i] & (1<<j))==0) bits[j].push_back(i);
            }
        }
        ll ans = 0;
        for(ll i=0; i<n; i++){
            ll val = arr[i];
            if(val < k) continue;
            vector<pair<ll,ll>> nxt;
            for(ll j=0; j<=30; j++){
                if((arr[i] & (1<<j)) == 0) continue;
                auto it = upper_bound(bits[j].begin(),bits[j].end(), i)-bits[j].begin();
                if(it == bits[j].size()) continue;
                nxt.push_back({bits[j][it], j});
            }
            sort(nxt.begin(),nxt.end());
            ll prev = i;
            for(auto it: nxt){
                ll idx = it.first;
                if(val == k) ans += idx-prev;
                prev = it.first;
                val ^= (1<<it.second);
            }
            if(val == k) ans += n-prev;
        }
        return ans;
    }
};
