class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0ll;
        vector<vector<int>>pos(32);
        for(int i=n-1;i>=0;i--){
            for(int j=0;j<32;j++){
                if((nums[i]&(1<<j))==0){
                    pos[j].push_back(i);
                }
            }
        }
        for(int j=0;j<32;j++)reverse(pos[j].begin(),pos[j].end());
        for(int i=0;i<n;i++){
            if(nums[i]&k!=k)continue;
             vector<pair<int,int>>temp;
             for(int j=0;j<32;j++){
                auto index=lower_bound(pos[j].begin(),pos[j].end(),i+1)-pos[j].begin();
                int s1=pos[j].size();
                if(s1==0)continue;
                if(index!=s1)temp.push_back({pos[j][index],j});
             }
             sort(temp.begin(),temp.end());
             int val=nums[i];
             int s1=temp.size();
             int last=i;
             for(int j=0;j<s1;j++){
                 int i1=temp[j].second;
                 int i2=temp[j].first;
                 if(val==k){
                      ans=ans+1ll*(i2-last);
                 }
                 val&=~(1<<i1);
                 last=i2;
             }
             if(val==k)ans=ans+1ll*(n-last);
        }
        return ans;
    }
};
