class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0,cnt=0;
        unordered_map<int,int> mp;
        for(int x:nums){
            unordered_map<int,int> tmp;
            for(auto&[y,_]:mp) tmp[y&x]+=mp[y];
            tmp[x]++;
            ans+=tmp[k];
            mp=move(tmp);
        }
        return ans;

    }
};
