class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        L = []
        ans = 0
        for i, num in enumerate(nums):
            for j in range(len(L)):
                L[j][0] = L[j][0] & num
            L.append([num, i])
            nxt_L = []
            for value, idx in L:
                if len(nxt_L) > 0 and value == nxt_L[-1][0]:
                    continue
                nxt_L.append([value, idx])
            L = nxt_L
            # print("L =", L)
            for j, (value, idx) in enumerate(L):
                if value == k:
                    if j == len(L) - 1:
                        ans += i - idx + 1
                    else:
                        ans += L[j + 1][1] - idx
                    break
        return ans
