class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> cnt;
        long long res = 0;
        for (int i = 0; i < nums.size(); ++i) {
            unordered_map<int, int> cnt1;
            if ((nums[i] & k) >= k) {
                if (nums[i] == k)
                    ++res;
                cnt1[nums[i]] = 1;
                for (auto [v, count] : cnt) {
                    if ((v & nums[i]) == k)
                        res += count;
                    if ((v & nums[i]) >= k)
                        cnt1[v & nums[i]] += count;
                }
            }
            swap(cnt, cnt1);
            // cout << i << " = " << res << endl;
        }
        return res;
    }
};
