class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums, k) - solve(nums, k+1);
    }

    long long solve(vector<int>& nums, int T){
        vector<int>ones(31);
        vector<int>zeros(31);

        int n = nums.size();
        long long ans = 0;
        int i=0, j=0;
        int x, y;
        while(j<n) {
            x = 0;
            for(int k=0;k<31;k++){
                if(nums[j]&(1<<k))
                    ones[k]++;
                else
                    zeros[k]++;
                if(ones[k]>0 && zeros[k]==0){
                    x += (1<<k);
                }
            }
            while(x<T && i<=j){
                y = 0;
                for(int k=0;k<31;k++){
                    if(nums[i]&(1<<k))
                        ones[k]--;
                    else
                        zeros[k]--;
                    if(ones[k]>0 && zeros[k]==0){
                        y += (1<<k);
                    }
                }
                x = y;
                i++;
            }
            ans += 1ll*(j-i+1);
            j++;
        }
        return ans;
    }

};
